function replaceWords($str)
{
	$str = preg_replace("/省$|市$|区$|县$/", '', $str);
}

function BuildAddress($addressinput, $flag=0){	//$flag不等于0时，去掉地址后面的"省"和"市"
	global $Province ,$City, $County;
	
	$strlen = strlen($addressinput);
	if ($strlen == 6)
	{
		$find = @array_search($addressinput,$Province);
		$findStr = $Province[$find+1];
		if ($flag)
			replaceWords($findStr);
		return $findStr;
	}
	
	if ($strlen == 9)
	{
		$key = substr($addressinput,0,6);
		$find = @array_search($addressinput,$City[$key]);
		$findStr = $City[$key][$find+1];
		if ($flag)
			replaceWords($findStr);
		return $findStr;
	}
	
	if ($strlen == 12)
	{
		$key = substr($addressinput,0,9);
		$find = @array_search($addressinput,$County[$key]);
		$findStr = $County[$key][$find+1];
		if ($flag)
			replaceWords($findStr);
		return $findStr;
	}
	return false;
}

function getArea($code, $link='.')
{
	$pv = $cy = $cu = '';
	$len = strlen($code);
	if ($len >= 6)
		$pv = BuildAddress(substr($code,0,6),0);
	if ($len >= 9)
		$cy = BuildAddress(substr($code,0,9),0);
	if ($len == 12)
		$cu = BuildAddress($code,0);
	
	return $pv.$link.$cy.$link.$cu;
}


function areaIdToName($id)
{
	$result = array('province' => '', 'city'=>'', 'County' => '');
	if(!$id) return $result;
	$result['province'] = BuildAddress(substr($id,0,6),1);
	$result['city'] = BuildAddress(substr($id,0,9),1);
	$result['County'] = BuildAddress($id,1); 
	return $result;
}

function areaIdToNameHave($id)  //带省，的后序的函数
{
	$result = array('province' => '', 'city'=>'', 'County' => '');
	if(!$id) return $result;
	$result['province'] = BuildAddress(substr($id,0,6),0);
	$result['city'] = BuildAddress(substr($id,0,9),0);
	$result['County'] = BuildAddress($id,0); 
	return $result;
}

function areaNameToId($pv, $cy, $cu)
{
	global $Province, $City, $County;
	
	if (empty($pv)) {
		return;
	}
	
	$result = array('province'=>0, 'city'=>0, 'county'=>0, 'complete' => 1);

    replaceWords($pv,1);
    replaceWords($cy,1);
    replaceWords($cu,1);

	/*查找省份*/
	$pf = false;
	foreach ($Province as $k=>$v) {
		if ($k%2) {
			if (preg_match("/^$pv/", $v)) {
				$pf = $k;
				break;
			}
		}
	}
	
	//$pf = array_search($pv, $Province);
	if(!$pf) {
		$pf = array_search($pv.'省', $Province);
		if($pf) {

			$result['province'] = $Province[$pf-1]; 
			findCity($cy, $cu, $result, $City, $County);
		}
		else {
			/* 找不到省份 */
			$result['complete'] = 0;
		}
	}
	else {
		$result['province'] = $Province[$pf-1]; 
		findCity($cy, $cu, $result, $City, $County);
	}

	return $result;
	
}

function findCity($cy, $cu, $result, $City, $County) {
	/*查找城市*/
	$pf = array_search($cy, $City[$result['province']]);
	if(!$pf) {
		$pf = array_search($cy.'市', $City[$result['province']]);
		if($pf) {
			$result['city'] = $City[$result['province']][$pf-1];
			findCounty($cu, $result, $County );

		}
		/* 找不到城市 */
		else {
			$result['complete'] = 0;
		}
	}
	else {
		$result['city'] = $City[$result['province']][$pf-1];
		
		findCounty($cu, $result, $County );
	}	
}

function findCounty($cu, $result, $County) {
	/*查找区*/
	if(isset($County[$result['city']]) && count($County[$result['city']]) >2) {
		/*该城市存在区，但没有输入*/
		if(!$cu) {
			$result['complete'] = 0;
			return;
		}
		$pf = array_search($cu, $County[$result['city']]);
		if(!$pf) {
			$pf = array_search($cu.'区', $County[$result['city']]);
			if(!$pf) {
				$pf = array_search($cu.'县', $County[$result['city']]);
				if($pf) {
					$result['County'] = $County[$result['city']][$pf-1];
				}
				else {
					/* 找不到区 */
					$result['complete'] = 0;
				}

			}
			else {
				$result['County'] = $County[$result['city']][$pf-1];
			}

		}
		else {
			$result['County'] = $County[$result['city']][$pf-1];
		}
	}
	else {
		$result['County'] = $result['city'];
	}
}
